import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { TouchableOpacity } from 'react-native-web';


const Task = (props) => {
    return (
      <View style={styles.item}>
        <View style={styles.itemLeft}>
          <View style={styles.square}></View>
          <Text style={styles.itemText}>{props.text}</Text> {/* Wrap props.text in a Text component */}
        </View>
        <Pressable onPress={props.onDelete}> {/* You can use Pressable here if needed */}
          <View style={styles.circular}></View>
        </Pressable>
      </View>
    );
  };

const styles = StyleSheet.create({
    itemLeft : {
        flexDirection: 'row',
        alignItems : 'center',
        flexWrap: 'wrap',
    },
    item: {
        backgroundColor: '#fff' ,
        padding: 15,
        borderRadius: 10,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: 20,
    },
    aquare: {

    },
    itemText :{
        maxWidth : '80%'
    },
    circular :{
        width :12,
        height: 12,
        borderColor: '#55BCF6',
        borderWidth: 2,
        borderRadius: 5,
    },
    square: {
        width:24,
        height: 24,
        backgroundColor: '#55BCF6',
        opacity: 0.4,
        borderRadius :5,
        marginRight : 15,
    }
});


export default Task;
