import { KeyboardAvoidingView, StyleSheet, Text, View, TextInput, TouchableOpacity, Keyboard, Pressable } from 'react-native';
import React, {useState, useEffect} from 'react';
import Task from './components/Task';
import AsyncStorage from '@react-native-async-storage/async-storage';


export default function App() {

  const [task, setTask]= useState();
  const [taskItems, setTaskItems] = useState([]);

  const handleAddTask = () => {
    if (task) {
      Keyboard.dismiss();
      const newTasks = [...taskItems, task];
      setTaskItems(newTasks);
      setTask('');


      AsyncStorage.setItem('tasks', JSON.stringify(newTasks))
        .catch(error => console.error('Error saving tasks:', error));
    }
  };

    useEffect(() => {
      loadTasks();
    }, []);

  const loadTasks = async () => {
    try {
      const savedTasks = await AsyncStorage.getItem('tasks');
      if (savedTasks !== null) {
        setTaskItems(JSON.parse(savedTasks));
      }
    } catch (error) {
      console.error('Error loading tasks:', error);
    }
  };

  return (
    <View style={styles.container}>

      {/* Today's Tasks */}
        <View style = {styles.taskWrapper}>
          <Text style ={styles.sectionTitle}>Today's tasks</Text>

          <View styles={styles.items}>
            {/* This is where the tasks will go! */}
            {taskItems.map((item, index) => {
                return (
                  <Task
                    key={index}
                    text={item}
                    onDelete={() => handleDeleteTask(index)}
                  />
                )
              })
            }
          </View>
        </View>

        {/*write a task section */}
        <KeyboardAvoidingView 
          behavior = {Platform.OS === 'ios' ? 'padding' : 'height'}
          style= {styles.writeTaskWrapper}>
            <TextInput style={styles.input} placeholder= {'Write a task'} value={task} onChangeText = {text => setTask(text)} />
            <Pressable onPress={()=> handleAddTask()}>
              <View style= {styles.addWrapper}>
                <Text style ={styles.addText}>+</Text>
              </View>
            </Pressable>
        </KeyboardAvoidingView>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EAED',
  },
  taskWrapper: {
    paddingTop: 80,
    paddingHorizontal : 20,
  },
  sectionTitle : {
    fontSize : 24,
    fontWeight : 'bold',
    marginBottom:10,
  },
  items : {
      marginTop:30,
  }, 
  addText :{

  },
  writeTaskWrapper: {
    position: 'absolute',
    bottom: 60,
    width: '100%',
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center'

  }, addWrapper: {
    width : 60,
    height: 60,
    borderRadius: 60,
    backgroundColor: '#fff',
    borderColor: '#c0c0c0',
    borderWidth: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
  input: {
    paddingVertical: 15,
    width: 300,
    paddingHorizontal: 15,
    backgroundColor: '#fff',
    borderRadius: 60,
    borderColor: '#c0c0c0',
    borderWidth: 1,

  }
});
